package _161_OneEditDistance;

import static org.junit.Assert.*;

import org.junit.After;
import org.junit.Before;
import org.junit.Rule;
import org.junit.Test;
import org.junit.rules.Timeout;

public class SolutionTest {
    
    /** Test method for {@link _161_OneEditDistance.Solution } */
    Solution solution;

    @Rule
    public Timeout globalTimeout = new Timeout(200);

    @Before
    public void setUp() throws Exception {
        solution = new Solution();
    }

    @After
    public void tearDown() throws Exception {
        solution = null;
    }

    @Test
    public void Test1() {
        String s = "";
        String t = "";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(!actual);
    }

    @Test
    public void Test2() {
        String s = "aa";
        String t = "ab";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(actual);
    }

    @Test
    public void Test3() {
        String s = "aa";
        String t = "a";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(actual);
    }

    @Test
    public void Test4() {
        String s = "aa";
        String t = "aaa";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(actual);
    }

    @Test
    public void Test5() {
        String s = "aa";
        String t = "cb";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(!actual);
    }

    @Test
    public void Test6() {
        String s = "adafjaa";
        String t = "cbadf";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(!actual);
    }

    @Test
    public void Test7() {
        String s = "cb";
        String t = "ab";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(actual);
    }

    @Test
    public void Test8() {
        String s = "teacher";
        String t = "tache";
        boolean actual = solution.isOneEditDistance(s, t);
        assertTrue(!actual);
    }

}
